const mongoose = require('mongoose')
const User = mongoose.model('User')
const DocumentRh = mongoose.model('documentRh')
var pdfMake = require('pdfmake/build/pdfmake');
var pdfFonts = require('pdfmake/build/vfs_fonts');
const cloudinary = require('cloudinary');
const nodemailer = require ('nodemailer')
const sendGridTransport = require ('nodemailer-sendgrid-transport');
const moment = require('moment');

const transporter = nodemailer.createTransport(sendGridTransport({
    sendMail: true,
    auth: {
        api_key: process.env.apiKey
    }
}))


cloudinary.config({
    cloud_name: process.env.cloud_name,
    api_key: process.env.api_cloudinary_key,
    api_secret: process.env.api_cloudinary_secret
})

pdfMake.vfs = pdfFonts.pdfMake.vfs
pdfMake.fonts = {
    // Default font should still be available
    Roboto: {
        normal: 'Roboto-Regular.ttf',
        bold: 'Roboto-Medium.ttf',
        italics: 'Roboto-Italic.ttf',
        bolditalics: 'Roboto-Italic.ttf'
    },
    // Make sure you define all 4 components - normal, bold, italics, bolditalics - (even if they all point to the same font file)
    TimesNewRoman: {
        normal: 'Times-New-Roman-Regular.ttf',
        bold: 'Times-New-Roman-Bold.ttf',
        italics: 'Times-New-Roman-Italics.ttf',
        bolditalics: 'Times-New-Roman-Italics.ttf'
    }
};

/** ask for a human resource document */
exports.ask_document = function (req, res) {
    const {type, titre, commentaire} = req.body
    if (!type | !titre) {
        return res.status(422).json({error: "Please add all the fields"})
    }
    //const dateDemandetest =  moment().format("DD MM YYYY")
    const AskedDocument = new DocumentRh ({
        type,
        titre,
        dateDemande: new Date(),
        commentaire,
        askedBy: req.user
    })
        AskedDocument.save()
        .then((askeddoc) => {
            res.send(askeddoc)
        })
        .catch((error) => {
            console.log(error)
        })
}

/** get my asked documents */
exports.get_my_documents = function (req, res) {
    DocumentRh.find({askedBy: req.user._id})
    .populate("askedBy", "nom prenom")
    .then((result) => {
        res.send(result)
    })
    .catch((error) => {
        console.log(error)
    })
}

