/** Responsable: get all employee demands */
exports.get_all_demands = function (req, res) {
    DocumentRh.find()
    .populate("askedBy", "nom prenom")
    .then((result) => {
        res.send(result)
    })
    .catch((error) => {
        console.log(error)
    })
}

/** Employé */
exports.delete_document = (req, res) => {
    const id = mongoose.Types.ObjectId(req.params.id)
    DocumentRh.findByIdAndDelete(id)
    .then((result) => {
        res.send(result)
    })
    .catch((error) => {
        console.log(error)
    })
}

/** Responsable */
exports.get_nonTreatedDocuments = (req, res) => {
    DocumentRh.find({askedBy: req.user._id})
    .populate("askedBy", "nom prenom")
    .then((result) => {
    const documents = result.filter(object => object.statut == "en attente")
       res.send(documents)
    })
    .catch((error) => {
        console.log(error)
    })
}

